        i0.ɵɵelement(282, "br");
        i0.ɵɵtext(283, " You have the right to withdraw your data protection declaration of consent at any time. Your withdrawal does not touch the lawfulness of data processing until the time of your withdrawal. ");
        i0.ɵɵelementEnd();
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(284, "li");
        i0.ɵɵelementStart(285, "p");
        i0.ɵɵelementStart(286, "strong");
        i0.ɵɵtext(287, "Automated Decision in Each Individual Case including Profiling");
